(window.webpackJsonp=window.webpackJsonp||[]).push([[24],{464:function(t,s,n){"use strict";n.r(s);var r=n(2),a=Object(r.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("p",[t._v("食品过敏和不耐受是两种不同的食品反应，它们在临床表现、发病机制和处理方法等方面存在差异。")]),t._v(" "),s("h2",{attrs:{id:""}},[s("a",{staticClass:"header-anchor",attrs:{href:"#"}},[t._v("#")])]),t._v(" "),s("p",[t._v("食品过敏是指身体对某些特定蛋白质产生过度敏感的反应，是一种免疫系统介导的过程。食物过敏反应可以发生在任何年龄，但是最常见的是在婴儿和幼儿期发生。食品过敏症状可能包括呼吸道症状（如喘息、呼吸急促、喉部紧缩）、皮肤症状（如荨麻疹、皮疹、瘙痒）、胃肠症状（如腹泻、呕吐、腹痛）以及其他全身症状（如头痛、低血压、昏厥）。食品过敏一般需要经过食物特异性IgE抗体的检测来诊断，通常需要避免引起过敏反应的食物。")]),t._v(" "),s("p",[t._v("不耐受则是指身体对某些食物成分难以消化或吸收的反应，是一种非免疫系统介导的过程。不耐受通常发生在成年人身上，而不是婴儿或儿童。不耐受症状包括胃肠不适、腹胀、腹泻、便秘和恶心等。不耐受的原因包括缺乏特定酶或对某些食物成分敏感。为了确诊不耐受，可以通过实验性地将特定食物从饮食中排除，并观察症状是否减轻来确定。")]),t._v(" "),s("p",[t._v("由于食品过敏和不耐受的临床表现相似，因此许多人可能会将它们混淆。但是，两者的处理方法是不同的。对于食品过敏者，需要彻底避免引起过敏反应的食物。而对于不耐受者，则可以根据症状和食物排除试验的结果来确定是否需要减少或避免特定的食物。此外，对于一些人来说，一些食品可能会引起类似过敏的反应，但并非真正的食品过敏。在这种情况下，进一步的诊断和治疗可能是必要的。")]),t._v(" "),s("p",[t._v("总之，食品过敏和不耐受都是常见的食品相关问题，但是两者的症状和原因是不同的。了解自己的情况，采取相应的措施可以有效缓解不适的症状，并保障自己的健康。如果出现严重症状，建议及时就医。")])])}),[],!1,null,null,null);s.default=a.exports}}]);